		}
		End
		{
			
			#Clean Variables
			Remove-Variable session -Force -ErrorAction SilentlyContinue
			Remove-Variable devDetail -Force -ErrorAction SilentlyContinue
			Remove-Variable hash -Force -ErrorAction SilentlyContinue
		}
